void ui_init(void)
{
    lv_disp_t * dispp = lv_disp_get_default();
    lv_theme_t * theme = lv_theme_default_init(dispp, lv_palette_main(LV_PALETTE_BLUE), lv_palette_main(LV_PALETTE_RED),
                                               false, LV_FONT_DEFAULT);
    lv_disp_set_theme(dispp, theme);
    ui_mainpage_screen_init();
    ui_sensorpage_screen_init();
    ui_weathercpage_screen_init();
    ui_musicpage_screen_init();
    ui_settingpage_screen_init();
    ui_servopage_screen_init();
    ui_airqpage_screen_init();
    ui_futurewpage_screen_init();
    ui____initial_actions0 = lv_obj_create(NULL);
    lv_disp_load_scr(ui_mainpage);
}
